  }
  // Left row
  for (let y = 1; y < h; y++) {
    data[y][0] = data[y - 1][0] + g(0, y);
  }
  // Inner
  for (let y = 1; y < h; y++) {
    for (let x = 1; x < w; x++) {
      data[y][x] = data[y - 1][x] + data[y][x - 1] - data[y - 1][x - 1] + g(x, y);
