function drawCircle(x, y, radius, isBlack, angle) {
  // Draw self
  angle += angleOff * (alternate ? (isBlack ? 1 : -1) : 1);
  context.fillStyle = isBlack ? "black" : "white";
  context.beginPath();
  context.moveTo(x, y);
  context.arc(x, y, radius, 0, 6.283);
  context.fill();
  // Draw children
  if (radius > 3) {
    const newRadius = radius * radiusFactor;
    const disFromCenter = radius - newRadius;
    const childAngle = angle;
    for (let i = 0; i < childCount; i++) {
      const dx = disFromCenter * Math.sin(angle), dy = disFromCenter * Math.cos(angle);
      drawCircle(x + dx, y + dy, newRadius, !isBlack, childAngle)
      angle += angleStep;
    }
  }
}

