const rulesetAlive = aliveRule.split(",").map(s => +s.trim());
const deadRule = _string("Dead Rule", "3");
const rulesetDead = deadRule.split(",").map(s => +s.trim());

const tileSize = 16;
setSize(tilesX * tileSize, tilesY * tileSize);

const tiles = range(tilesY).map(v => range(tilesX).map(_ => false));
const neighbors = tiles.map(row => row.map(v => 0));

const updateStep = 250;
let tileStateToSet = true;
let playing = false;
let nextUpdate = 0;

animate((dt, t, { mouse, getKey }) => {
  drawGame();
  if (mouse.inside) {
    const tx = Math.floor(mouse.x / tileSize),
      ty = Math.floor(mouse.y / tileSize);
    if (mouse.left.down) {
      tileStateToSet = !tiles[ty][tx];
    }
    if (mouse.left.pressed) {
      tiles[ty][tx] = tileStateToSet;
    }
  }
  if (getKey(" ").down) {
    playing = !playing;
    console.log(playing ? "Resumed" : "Paused");
  }
  if (playing) {
    if (t >= nextUpdate || t < nextUpdate - updateStep) {
      nextUpdate = t + updateStep;
      updateGame();
    }
  }
}, 86400000);


function drawGame() {
  fill("white");
  forAllTiles((x, y) => {
    context.fillStyle = tiles[y][x] ? "black" : "white";
    context.fillRect(x * tileSize, y * tileSize, tileSize - 1, tileSize - 1);
  });
}

function updateGame() {
  // Step 1: Count neighbors
  forAllTiles((x, y) => {
    neighbors[y][x] = 0;
    forRange(x - 1, x + 2, y - 1, y + 2, (nx, ny) => {
      if (nx !== x || ny !== y) {
        neighbors[y][x] += (tiles[ny][nx] ? 1 : 0);
      }
    });
  });
  // Step 2: Update states
  forAllTiles((x, y) => {
    const ruleset = tiles[y][x] ? rulesetAlive : rulesetDead;
    tiles[y][x] = ruleset.includes(neighbors[y][x]);
  });
}

function forAllTiles(func) {
  forRange(0, tilesX, 0, tilesY, func);
}

function forRange(xFrom, xTo, yFrom, yTo, func) {
  xFrom = Math.max(0, xFrom);
  xTo = Math.min(tilesX, xTo);
  yFrom = Math.max(0, yFrom);
  yTo = Math.min(tilesY, yTo);
  for (let y = yFrom; y < yTo; y++) {
    for (let x = xFrom; x < xTo; x++) {
      func(x, y);
    }
  }
}

